import { Component, OnDestroy, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { Role } from 'src/app/enum/Role';
import { JwtResponse } from 'src/app/response/JwtResponse';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-navigation',
  templateUrl: './navigation.component.html',
  styleUrls: ['./navigation.component.css']
})
export class NavigationComponent implements OnInit, OnDestroy {


  currentUserSubscription: Subscription | undefined;
  name$: Subscription | undefined;
  name: string | undefined;
  currentUser: JwtResponse | undefined;
  root = '/';
  Role = Role;

  constructor(private userService: UserService,
              private router: Router,
  ) {

  }


  ngOnInit() {
      this.name$ = this.userService.name$.subscribe(aName => this.name = aName);
      this.currentUserSubscription = this.userService.currentUser.subscribe(user => {
          this.currentUser = user;
          if (!user || user.role == Role.Customer) {
              this.root = '/';
          } else {
              this.root = '/seller';
          }
      });
  }

  ngOnDestroy(): void {
    if (this.currentUserSubscription) {
      this.currentUserSubscription.unsubscribe();
  }
    // this.name$.unsubscribe();
  }

  logout() {
      this.userService.logout();
      // this.router.navigate(['/login'], {queryParams: {logout: 'true'}} );
  }

}
<nav class="navbar navbar-expand-lg navbar-dark bg-dark">
    <a class="navbar-brand" [routerLink]="root">
        <img src="/assets/logo.png" class="d-inline-block align-top" alt="">
        Keyu Han - online Shop
    </a>

    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav"
            aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
    </button>

    <div class="collapse navbar-collapse" id="navbarNav">

        <a class="nav-item nav-link "
        routerLink="/category/0">
         Books
     </a>
     <a class="nav-item navbar-dark nav-link"
        routerLink="/category/1">
         Food
     </a>
     <a class="nav-item nav-link "
        routerLink="/category/2">
         Clothes
     </a>
     <a class="nav-item nav-link"
        routerLink="/category/3">
         Drink
     </a>
    </div>

    <div class="navbar-nav ml-auto">
        <a *ngIf="!currentUser || currentUser.role == Role.Customer"
                class="nav-item nav-link " routerLink="/cart">
            <i class="fas fa-shopping-cart"></i>
            Cart
        </a>
        <ng-container *ngIf="currentUser; else noUser">
            <a class="nav-item nav-link " routerLink="/order">
                <i class="fas fa-list-ul"></i>
                Orders
            </a>
            <a class="nav-item nav-link " routerLink="/profile">
                {{name}}
            </a>
            <a class="nav-item nav-link " (click)="logout()" routerLink="/login" [queryParams]="{logout: true}">
                Sign Out
            </a>
        </ng-container>
        <ng-template #noUser>
            <a class="nav-item nav-link " routerLink="/login">
                Sign In
            </a>
            <a class="nav-item nav-link " routerLink="/register">
                Sign Up
            </a>
        </ng-template>
    </div>
</nav>
